mapper.insert:
INSERT INTO acorncalendar.JH_FILE
	(F_TYPE, F_TYPE_SEQ, F_OG_NAME, F_SV_NAME, F_SIZE, F_MAIN_YN, F_DEL_YN)
VALUES(#{fType}, #{fTypeSeq}, #{fOgName}, #{fSvName}, #{fSize}, 'Y', 'N');

mapper.select:
SELECT COUNT(1)
FROM JH_MEMBER
WHERE M_ID = #{mId}

SELECT COUNT(1)
FROM JH_MEMBER
WHERE M_NICKNAME = #{mNickname}

SELECT COUNT(1)
FROM JH_MEMBER
WHERE M_EMAIL = #{mEmail}

mapper.update:
UPDATE JH_MEMBER
SET M_DEL_YN = 'Y'
WHERE M_SEQ = #{m_seq}

mapper.select:
CALL PROC_UPDATE_VACT_CNT(
#{P_SEQ, 			mode=IN, 	jdbcType=numeric,	javaType=Integer},
#{P_JOIN_COMP_DT, 	mode=IN, 	jdbcType=DATE,		javaType=Date},
#{RSLT,				mode=OUT,	jdbcType=VARCHAR,	javaType=String}
)
